import { ITableData, Point, IHole, IFloorBaseData,  } from './IInterface'
import { isPointInPolygon } from './math'

/**
 * 判断点是否在楼梯内部
 * @param point 
 * @param floorBaseData 
 * @returns 
 */
function isPointInFloor(point: Point, floorBaseData: IFloorBaseData) {
    let { shape, holes } = floorBaseData
    for(let i = 0; i < holes.length;i++) {
        let hole: IHole = holes[0]
        if(isPointInPolygon(point, hole)) {
            return false
        }
    }
    if(isPointInPolygon(point, shape)) {
        return true
    } else {
        return false
    }
}

function getDrawTableData(data: ITableData[]): boolean {
    return false
}

export {
    getDrawTableData,
    isPointInFloor
}
