import React from 'react'
import {
    IPlaneProps,
    IFloorBaseData,
    Point,
    ITableData
} from './IInterface'
import { isPointInFloor } from './utils'
// import { isPointInPolygon } from './math'
// import { rgb } from 'd3'

let floorBaseData: IFloorBaseData = {
    shape: [
        [100, 100], 
        [340, 100],
        [340, 200],
        [300, 200],
        [300, 380],
        [100, 380],
    ],
    holes: [
        [
            [120, 120],
            [160, 120],
            [160, 180],
            [120, 180]
        ],
        [
            [120, 220],
            [140, 220],
            [140, 240],
            [120, 240]
        ]
    ]
}

let tableData: ITableData[] = [
    {
        user: '315103',
        tableid: '6-6-201',
        shape: [
            [150, 160],
            [160, 160],
            [160, 170],
            [150, 170]
        ]
    },
    {
        user: '315104',
        tableid: '6-6-204',
        shape: [
            [170, 160],
            [180, 160],
            [180, 170],
            [170, 170]
        ]
    }
]

class Plane extends React.Component<IPlaneProps> {
    private canvas!: HTMLCanvasElement;
    private ctx!: CanvasRenderingContext2D;
    private width: number;
    private height: number;
    private top: number;
    private left: number;

    constructor(props: IPlaneProps) {
        super(props)
        this.width = 100;
        this.height = 100;
        this.top = 0;
        this.left = 0;
        this.handleClick = this.handleClick.bind(this)
    }

    componentDidMount() {
        this.initProperties()

        // console.log(rgb('rgb(1, 2, 3)'))
        this.draw()


        this.canvas.addEventListener('click', this.handleClick, false)

    }

    componentWillUnmount() {
        this.canvas.removeEventListener('click', this.handleClick)
    }

    initProperties() {
        let { width, height } = this.props
        this.width = width 
        this.height = height
        this.ctx = this.setCanvas(width, height)
        let { top, left } = this.canvas.getBoundingClientRect()
        this.top = top
        this.left = left
    }

    draw() {
        this.drawFloorBase(this.ctx, floorBaseData, '#00f', '#0ff')
        // this.drawTables(this.ctx, tableData, '#f00', '#0f0')
    }

    handleClick(e: MouseEvent) {
        let {clientX, clientY} = e
        let x = clientX - this.left
        let y = clientY - this.top


        let b = isPointInFloor([x, y], floorBaseData)
        console.log(b)
    }

    drawTables(ctx: CanvasRenderingContext2D, tableData: ITableData[], strokeStyle: string, fillStyle: string) {
        ctx.strokeStyle = strokeStyle
        ctx.fillStyle = fillStyle

        tableData.map((td: ITableData) => {
            let { shape } = td
            // draw line
            ctx.beginPath()
            shape.map((s: Point): void => {
                ctx.lineTo(...s)
            })
            ctx.lineTo(...shape[0])
            ctx.closePath()
            ctx.fill()
            ctx.stroke()
        })
    }

    // 绘制楼层 - 不需要被选中
    drawFloorBase(ctx: CanvasRenderingContext2D, floorBaseData: IFloorBaseData, strokeStyle: string, fillStyle: string) {
        let { shape, holes } = floorBaseData

        ctx.strokeStyle = strokeStyle
        ctx.fillStyle = fillStyle

        // draw line
        ctx.beginPath()
        shape.map((s: Point): void => {
            ctx.lineTo(...s)
        })
        ctx.lineTo(...shape[0])
        ctx.closePath()
        ctx.fill()
        ctx.stroke()

        // draw holes
        holes.map(hole => {
            ctx.beginPath()
            ctx.save()

            hole.map((h: Point) => {
                ctx.lineTo(...h)
            })
            ctx.lineTo(...hole[0])

            ctx.clip()
            ctx.clearRect(0, 0, this.width, this.height)

            ctx.closePath()
            ctx.restore()
            ctx.stroke()
        })
    }

    drawBalcany() {

    }

    setCanvas(width: number, height: number): CanvasRenderingContext2D {
        this.canvas.style.width = width + 'px'
        this.canvas.style.height = height + 'px'
        this.canvas.width = width
        this.canvas.height = height
        return this.canvas.getContext('2d') as CanvasRenderingContext2D
    }

    render() {
        return <canvas ref={el=>this.canvas = el as HTMLCanvasElement}></canvas>
    }
}

export default Plane
